mapper.select:
SELECT

FROM light_conf_conf conf INNER JOIN light_conf_app_conf app_conf ON conf.id = app_conf.conf_id
WHERE app_conf.app_id = #{appId,jdbcType=INTEGER};

SELECT

FROM light_conf_conf conf INNER JOIN light_conf_app_conf app_conf ON conf.id = app_conf.conf_id
WHERE app_conf.app_id = #{appId,jdbcType=INTEGER}

    AND conf.conf_key LIKE #{confKey,jdbcType=VARCHAR}

limit #{start},#{length}

SELECT count(*)
FROM light_conf_conf conf INNER JOIN light_conf_app_conf app_conf ON conf.id = app_conf.conf_id
WHERE app_conf.app_id = #{appId,jdbcType=INTEGER}

    AND conf.conf_key LIKE #{confKey,jdbcType=VARCHAR}
